function updateTime(inputs, outputs) {
    var d = new Date();

    var self = this;
    function setOutput(name,value) {
        if(outputs[name] !== value) {
            outputs[name] = value;
            self.flagOutputDirty(name);
        }
    }

    setOutput("second", d.getSeconds());
    setOutput("minute", d.getMinutes());
    setOutput("hour", d.getHours());
    setOutput("day", d.getDate());
    setOutput("month",  d.getMonth());
    setOutput("year",  d.getFullYear());
}

define({
    outputs: {
        hour: "number",
        minute: "number",
        second: "number",
        day: "string",
        month: "string",
        year: "string"
    },
    setup: function(inputs, outputs) {
        setInterval(updateTime.bind(this, inputs, outputs), 1000);
        updateTime.call(this, inputs, outputs);
    }
});

